import {makeAutoObservable} from 'mobx' //изменения 


export default class PhotoStore{
    constructor(){
        this._types =[
            {id:1, name:'Ваши фото'},
            { id:2, name:'Популярные фото'},
            { id:3, name:'Фото друзей'},
            { id:2, name:'Фото природы'},
            { id:2, name:'Смешные фото'},
        ]
        this._photo =[
            {id:1, name: "Фотография любимой",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1' },
            {id:2, name: "Фотография себя",img:'https://funart.pro/uploads/posts/2021-03/1617048969_52-p-oboi-krasivie-peizazhi-prirodi-56.jpg'},
            {id:3, name: "Фотография себя",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1'},
            {id:4, name: "Фотография себя",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1'},
            {id:5, name: "Фотография себя",img:'https://workspace.ru/upload/resize_cache/main/da2/240_240_2619711fa078991f0a23d032687646b21/photo_2021_11_13_01_37_45.jpg'},
            {id:6, name: "Фотография себя",img:'https://workspace.ru/upload/resize_cache/main/da2/240_240_2619711fa078991f0a23d032687646b21/photo_2021_11_13_01_37_45.jpg'},
        ]

        makeAutoObservable(this)//слежка за изменениями

    }//при изменении перерендеринг

    setTypes(bool){
        this._types = bool
    }

    setPhoto(user){
        this._photo = user
    }
    setSelectedType(type) {
        this.setPage(1)
        this._selectedType = type
    }
    setPage(page) {
        this._page = page
    }
    setTotalCount(count) {
        this._totalCount = count
    }
    get types() {
        return this._types
    }
    get devices() {
        return this._devices
    }
    get selectedType() {
        return this._selectedType
    }

    get totalCount() {
        return this._totalCount
    }
    get page() {
        return this._page
    }
    get limit() {
        return this._limit
    }
    //компьютед функции, вызываются только при изменении
    get isTypes(){
        return this._types
    }
    get isPhoto(){
        return this._photo
    }


}
