import React, {useContext, useEffect, useState} from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Dropdown, Form, Row, Col} from "react-bootstrap";
import {Context} from "../index";
import {createPhoto ,fetchTypes} from "../http/photoAPI";
import {observer} from "mobx-react-lite";
import Photogallery from '../pages/photoGallery';
import { PHOTOGALLERY_ROUTE } from '../utils/consts';
import { useHistory } from 'react-router-dom';

const CreatePhoto = observer(({show, onHide}) => {
    
    const {photo} = useContext(Context)
    const history = useHistory()
    const [name, setName] = useState('')
    const [file, setFile] = useState(null)
    const [info, setInfo] = useState([])

    const addInfo = () => {
        setInfo([...info, {title: '', description: '', number: Date.now()}])
    }
    const removeInfo = (number) => {
        setInfo(info.filter(i => i.number !== number))
    }
    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? {...i, [key]: value} : i))
    }

    const selectFile = e => {
        setFile(e.target.files[0])
    }

    const addPhoto = () => {
        const formData = new FormData()
        formData.append('name', name)
        formData.append('img', file)
        formData.append('typeId', 'Ваше фото')
        formData.append('userId', 4)
        //formData.append('info', JSON.stringify(info))
        createPhoto(formData).then(data => onHide())
        //console.log(formData)
        alert(`Фото ${name} успешно добавлено!`)
        history.push(PHOTOGALLERY_ROUTE)
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить фото
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                        placeholder="Введите название фото"
                    />
                    <Form.Control
                        className="mt-3"
                        type="file"
                        onChange={selectFile}
                    />
                    <hr/>
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide}>Закрыть</Button>
                <Button variant="outline-success" onClick={addPhoto}>Добавить</Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreatePhoto;
