import {$authHost, $host} from "./index";
import jwt_decode from "jwt-decode"; //для раскодировки jwt токена

export const registration = async (email, password) => {
    const {data} = await $host.post('api/user/registration', {email, password})//ответ, который будет возвращаться из сервера
    localStorage.setItem('token', data.token)
    return jwt_decode(data.token)
}

export const login = async (email, password) => {
    const {data} = await $host.post('api/user/login', {email, password})
    localStorage.setItem('token', data.token)//добавление токена в локальное хранилище
    return jwt_decode(data.token)
}

export const check = async () => {
    const {data} = await $authHost.get('api/user/auth' )
    localStorage.setItem('token', data.token)
    return jwt_decode(data.token)
}
