import React, {useEffect, useState} from 'react';
import {Col, Container, Image, Row} from "react-bootstrap";
import {useParams} from 'react-router-dom'
import {fetchOnePhoto} from "../http/photoAPI";



const PhotoPage = () => {
    const [Photo, setPhoto] = useState({info: []})
    const {id} = useParams()

    useEffect(() => {
        fetchOnePhoto(id).then(data => setPhoto(data))
    }, [])

    return (
        <Container className="mt-5">
            <Row>
                <Col md={6}>
                    <Image width={500} height={500} src={'http://localhost:5000/' + Photo.img}/>
                </Col>
                <Col md={6}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{Photo.name}</h2>
                    </Row>
                    <Row>Владелец фотографии {Photo.userId} </Row>
                <Row className="d-flex flex-column m-3">
                    <h4>Описание фотографии</h4>
                    {Photo.info.map((info, index) =>
                        <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                            {info.title}: {info.description}
                        </Row>
                    )}
            </Row>
                </Col>
            </Row>
            
        </Container>
    );
};


export default PhotoPage;
