import {makeAutoObservable} from 'mobx' //изменения 
import { fetchPhotos } from "../http/photoAPI";
import { useContext, useEffect } from "react";
import { Context } from "../index";



export default class PhotoStore {
    
    constructor(){
        
        this._types = []
        this._photo = []
        this._selectedType = {}
        this._page = 1
        this._totalCount = 0
        this._limit = 8
        this._last={}
        makeAutoObservable(this)//слежка за изменениями
        
    }
    //при изменении перерендеринг

    setTypes(types) {
        this._types = types
    }

    setPhotos(photo) {
        this._photo = photo
    }

    setSelectedType(type) {

        this.setPage(1)
        this._selectedType = type
        //console.log(this._count)
        if (this._selectedType != this._last){
            this._last = this._selectedType

            //console.log('Ок')
            }
        else if (this._selectedType == this._last){
            //console.log('Брух')
            this._selectedType =null    
        }
    }

    setPage(page) {
        this._page = page
    }
    
    setTotalCount(count) {
        this._totalCount = count
    }

    //компьютед функции, вызываются только при изменении
    get types() {
        return this._types
    }

    get photos() {
        return this._photo
    }
    get selectedType() {
        return this._selectedType
    }
    get totalCount() {
        return this._totalCount
    }
    get page() {
        return this._page
    }
    get limit() {
        return this._limit
    }
}
