        this._photo = []
        this._selectedType = {}
        this._page = 1
        this._totalCount = 0
        this._limit = 3
        makeAutoObservable(this)//слежка за изменениями

    }//при изменении перерендеринг

    setTypes(types) {
        this._types = types
    }

    setPhotos(photo) {
        this._photo = photo
    }

    setSelectedType(type) {
        this.setPage(1)
        this._selectedType = type
        alert(`Успешно ${type}`)
    }

    setPage(page) {
        this._page = page
    }
    
    setTotalCount(count) {
        this._totalCount = count
    }

    //компьютед функции, вызываются только при изменении
    get types() {
        return this._types
    }

    get photos() {
        return this._photo
    }
    get selectedType() {
        return this._selectedType
    }
    get totalCount() {
        return this._totalCount
    }
    get page() {
        return this._page
    }
    get limit() {
        return this._limit
    }




}
