            {id:1, name: "Фотография любимой",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1',type:'Ваши фото' },
