            {id:3, name: "Фотография себя",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1',type:'Ваши фото'},
            {id:4, name: "Фотография себя",img:'https://sun1-96.userapi.com/impg/20PbSQ8OLMbvCrSYXLkQwjzuuMAKI7TtEQPPFA/M9zNQATacD0.jpg?size=400x0&quality=96&crop=129,180,1487,1487&sign=5ea76c6f1a79f188dc5f9629d015708c&c_uniq_tag=GK_otowe8HBlMhMcFnJAx6AvgM6SRXUCzj18j4jtjsg&ava=1',type:'Ваши фото'},
            {id:5, name: "Фотография себя",img:'https://workspace.ru/upload/resize_cache/main/da2/240_240_2619711fa078991f0a23d032687646b21/photo_2021_11_13_01_37_45.jpg',type:'Ваши фото'},
            {id:6, name: "Фотография себя",img:'https://workspace.ru/upload/resize_cache/main/da2/240_240_2619711fa078991f0a23d032687646b21/photo_2021_11_13_01_37_45.jpg',type:'Фото друзей'},

        ]
        this._selectedType = {}
        makeAutoObservable(this)//слежка за изменениями

    }//при изменении перерендеринг

    setPage(page) {
        this._page = page
    }



    setTypes(bool){
        this._types = bool
    }

    setPhoto(user){
        this._photo = user
    }
    setSelectedType(type) {
        this.setPage(1)
        console.log(type)
        this._selectedType = type
    }

    //компьютед функции, вызываются только при изменении
    get isTypes(){
        return this._types
    }
    get isPhoto(){
        return this._photo
    }




}
