import React, {useEffect, useState, useContext} from 'react';
import {Col, Container, Image, Row} from "react-bootstrap";
import {useParams} from 'react-router-dom'
import {fetchOnePhoto, fetchNameUser} from "../http/photoAPI";
import {Context} from "../index";


const PhotoPage = () => {
    const [Photo, setPhoto] = useState({info: []})
    const [User, setUser] = useState('')
    const {id} = useParams()

    useEffect(() => {
        fetchOnePhoto(id).then(data => setPhoto(data))
    }, [])

    useEffect(() => {
        if (Photo.userId !==undefined)
            fetchNameUser(Photo.userId).then(data => setUser(data))
    }, [])


    return (
        <Container className="mt-5">
            <Row>
                <Col md={6}>
                    <Image width={500} height={500} src={'http://localhost:5000/' + Photo.img}/>
                </Col>
                <Col md={6}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{Photo.name}</h2>
                        <Row className="ml-3">Владелец фотографии: {User.email}</Row>
                    </Row>
                    
                <Row >
                    <h4 className='mt-4'>Описание фотографии</h4>
                    {Photo.info.map((info, index) =>
                        <Row key={info.id} style={{background: index % 2 === 0 ? 'lightgray' : 'transparent', padding: 10}}>
                            {info.title}: {info.description}
                        </Row>
                    )}
                </Row>
                </Col>
            </Row>
            
        </Container>
    );
};


export default PhotoPage;
